angular.module("ngCheckbox",[]).directive("checkbox",["$rootScope",function(e){return{restrict:"E",replace:!0,template:"<div></div>",scope:{ngModel:"=",size:"="},link:function(o,c){o.ngModel=!1,c.css({position:"relative",display:"inline-block",width:scope.size+"px",height:scope.size+"px","background-color":"#fff","border-radius":Math.floor(.2*scope.size)+"px",cursor:"pointer"}),c.on("click",function(){e.$apply(function(){o.ngModel=!o.ngModel}),c.css(scope.ngModel?{"background-image":"url(./check.png)","background-size":scope.size+"px"}:{"background-image":"","background-size":scope.size+"px"})})}}}]);